// Second Part

// TERRIBLE CODE AHEAD!!! I tried to continue doing the stuff of the "infinte" grid
// and the need to reajuste the other santa position really messed with the code
let grid = [[1]];

let santa = {
  x: 0,
  y: 0,
};

let robo_santa = {
  x: 0,
  y: 0,
};

let current_santa = santa;

[...input].forEach((e, index) => {
  let other_santa = current_santa == santa ? robo_santa : santa;

  if (e == "^") {
    current_santa.y--;
    if (current_santa.y == -1) {
      grid.unshift([]);
      current_santa.y = 0;
      other_santa.y++;
    }
  } else if (e == ">") {
    current_santa.x++;
  } else if (e == "v") {
    current_santa.y++;
    if (!grid[current_santa.y]) {
      grid.push([]);
      other_santa.y - 1 == -1 ? (other_santa.y = 0) : other_santa--;
    }
  } else {
    current_santa.x--;
    if (current_santa.x == -1) {
      grid.forEach((e) => e.unshift(0));
      current_santa.x = 0;
      other_santa.x++;
    }
  }

  if (!grid[current_santa.y][current_santa.x]) {
    grid[current_santa.y][current_santa.x] = 0;
  }
  grid[current_santa.y][current_santa.x]++;

  current_santa = current_santa == santa ? robo_santa : santa;
});

const visited_houses = grid.reduce((previous, current) => {
  console.log(current.length);
  const visited_houses_row = current.reduce((previous, current) => {
    // console.log(current, previous, previous + 1)
    if (current >= 1) {
      return previous + 1;
    }
    return previous;
  }, 0);
  console.log("visitadas: " + visited_houses_row);
  //   console.log("total: " + (previous + visited_houses_row));

  return previous + visited_houses_row;
}, 0);

console.log("casas visitadas: " + visited_houses);
